def x2_func(expression, result_var):
    if expression:
        try:
            expr = expression.replace(' ', '')
            result = eval(expr) ** 2
            formatted = str(result)
            return formatted, formatted
        except:
            return expression, 'Ошибка'
    return expression, expression

